$(document).ready(function() {
    $('[data-toggle="tooltip"]').tooltip();
});

function openMenu() {
    document.getElementById("mobile-menu").style.width = "60%";
    document.getElementById("mobile-stats").style.width = "0";
}
function closeMenu() {
    document.getElementById("mobile-menu").style.width = "0";
}
function openStats() {
    document.getElementById("mobile-stats").style.width = "60%";
    document.getElementById("mobile-menu").style.width = "0";
}
function closeStats() {
    document.getElementById("mobile-stats").style.width = "0";
}